mapper.select:


     and list_id = #{listId}
     and board_id = #{boardId}
     and card_title like concat('%', #{cardTitle}, '%')
     and priority = #{priority}
     and status = #{status}
     and executor_id = #{executorId}

order by order_num asc, create_time desc

